	// 			: setError("Error while fetching data");
	// 	}
	// };

	const searchValue = (e) => {
		setSearch(e.target.value);
		// console.log(search);
	};

	const onSearch = () => {
		setGetval(search);
		console.log(getval);
	};

	useEffect(() => {
		getData();
	}, [getval]);

	console.log(getval);
	//

	http: return (
		<div>
			{/* <Weather_info /> */}
			<div className="name container">
				<div className="search">
					<input type="text" placeholder="Search" onChange={searchValue} />
					<button type="submit" className="search_btn" onClick={onSearch}>
						Search Weather
					</button>
				</div>
				<p>{error}</p>
				<p className="c-name">{city}</p>
				<i className="fa fa-solid fa-cloud" aria-hidden="true"></i>
				<h3>Cloudy</h3>
				<h4>Sunday</h4>
				<p className="degree">
					20Â°<span>C</span>
				</p>
			</div>
			{city}
		</div>
	);
};

