/** @format */

import React, { useEffect, useState } from "react";
import Weather_info from "./components/Home/Weather_info";

const Config = () => {
	const [city, setCity] = useState("");
	const [search, setSearch] = useState("Lahore");
	const [error, setError] = useState("");
	const [getval, setGetval] = useState("Lahore");

	const searchValue = (e) => {
		setSearch(e.target.value);
		// console.log(search);
	};

	const onSearch = () => {
		setGetval(search);
		console.log(getval);
	};

	const getData = () => {
		fetch(
			`https://api.openweathermap.org/data/2.5/weather?q=${getval}&APPID=4c197cc5d752cd9a6e7afa80aa1b3f1d`,
			{
				method: "GET",
			},
		)
			.then((req) => req.json())
			.then(
				(result) => {
					// setIsLoaded(true);
					setCity(result);
					console.log(result);
				},
				// Note: it's important to handle errors here
				// instead of a catch() block so that we don't swallow
				// exceptions from actual bugs in components.
				(error) => {
					// setIsLoaded(true);
					setError(error);
				},
			);
	};

	useEffect(() => {
		{
			getData();
		}
	}, [getval]);

	console.log(getval);
	//

	http: return (
		<div>
			<div className="name container">
				<div className="search">
					<input type="text" placeholder="Search" onChange={searchValue} />
					<button type="submit" className="search_btn" onClick={onSearch}>
						Search Weather
					</button>
				</div>
				<p></p>
				<p className="c-name">
					{!city.name ? <span> {city}</span> : <span> {city.name}</span>},
					{!city.sys.country ? (
						<span> IN</span>
					) : (
						<span> {city.sys.country}</span>
					)}
				</p>
				<i className="fa fa-solid fa-cloud" aria-hidden="true"></i>
				<h3>Cloudy</h3>
				<h4>Sunday</h4>
				<p className="degree">
					20°<span>C</span>
				</p>
				<p>{error}</p>
			</div>
		</div>
	);
};

export default Config;
