/** @format */

import React, { useEffect, useState } from "react";
import Temp from "./components/Home/Temp";
import Weather_info from "./components/Home/Weather_info";

const Config = () => {
	// get value from input
	const [search, setSearch] = useState("Lahore");
	// save search value on getval
	const [getval, setGetval] = useState("Lahore");
	const [city, setCity] = useState("");
	const [error, setError] = useState("");
	const [getDate, setGetdate] = useState("");

	const searchValue = (e) => {
		setSearch(e.target.value);
		// console.log(search);
	};

	const onSearch = () => {
		setGetval(search);
		console.log(getval);
	};

	const getData = () => {
		fetch(
			`https://api.openweathermap.org/data/2.5/weather?q=${getval}&APPID=b946c4fd34369ee6ee93080c9ac53218`,
			{
				method: "GET",
			},
		)
			.then((req) => req.json())
			.then(
				(result) => {
					// setIsLoaded(true);
					setCity(result);
					// console.log(result.timezone);
					setGetdate(new Date(result.dt * 1000 - result.timezone * 1000));

					// console.log(new Date(result.dt * 1000 - result.timezone * 1000)); // minus
				},
				// Note: it's important to handle errors here
				// instead of a catch() block so that we don't swallow
				// exceptions from actual bugs in components.
				(error) => {
					// setIsLoaded(true);
					setError(error);
				},
			);
	};

	useEffect(() => {
		{
			getData();
		}
	}, []);

	console.log(getDate);

	return (
		<div>
			<div className="name container">
				<div className="row">
					<div className="col-12">
						<div className="search">
							<input type="text" placeholder="Search" onChange={searchValue} />
							<button type="submit" className="search_btn" onClick={onSearch}>
								Search Weather
							</button>
							{/* {city} */}
						</div>
						<div className="col-6 weather-info">
							<h1>London, UK</h1>
							<div class="date">
								<h2>Monday 01 March</h2>
								<h2>01:55pm</h2>
							</div>
							<div class="temp">
								<i class="fa fa-solid fa-sun"></i>
								<div class="centig">
									<p class="centi">20°</p>
									<p class="sky">Mostly Sunny</p>
								</div>
							</div>
						</div>
						<div className="col-6">
							<div class="humi">
								<div class="detail">
									<Temp value="55" station="high" />
									<Temp value="55" station="high" />
									<Temp value="55" station="high" />

									<Temp value="55" station="high" />
								</div>
							</div>
						</div>
						<i className="fa fa-solid fa-cloud" aria-hidden="true"></i>
						<h3>Cloudy</h3>
						<h4>Sunday</h4>
						<p className="degree">
							20°<span>C</span>
						</p>
						<p>{error}</p>
					</div>
				</div>

				{/* <p>{date}</p> */}
				{/* <p className="c-name">
					{city == "" ? getval : city.name},
					<span> {city == "" ? "PK" : city.sys.country}</span>
				</p> */}
			</div>
		</div>
	);
};

export default Config;
